export const GCOLOR = {
  PRIMARY: "#333335",
  SECONDARY: "#333335",
  TERTIER: "#dcdcdc",
  GRAY: "#121212",
};

/* Media Queries   0    1    2    3    4 */
export const GMQ = [320, 576, 768, 992, 1200];

export const GSPACER = {
  GUTTER: ["0.7rem", "0.95rem", "1rem", "1.2rem", "1.4rem"],
};

export const GFONT = {
  SIZE: [
    "calc(0.4em  + 0.50vw)",
    "calc(0.7em  + 0.70vw)",
    "calc(0.8em  + 0.85vw)",
    "calc(0.9em  + 1.05vw)",
    "calc(1.0em  + 1.20vw)",
    "calc(1.1em  + 1.45vw)",
  ],
};

export const mq = GMQ.map((mq) => `@media (min-width: ${mq}px)`);
