/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unused-vars */
import React, { useContext, useEffect, useState } from "react";
import {
  PokemonContext,
  PokemonsListContext,
} from "../../contexts/PokemonContext";
import { gqlQuery } from "../Querying";

import { css } from "@emotion/css";
import { GFONT, GSPACER, mq } from "../../styles/globalStyle";
import { Button } from "../../styles/button";
import { Container } from "../../styles/container";

const PokemonListPage = () => {
  const [totalPokemons, setTotalPokemons] = useState(0);
  const [offset, setOffset] = useState(1);
  const [limit, setLimit] = useState(20);
  const [result, setResult] = useState();
  const { pokemons, dispatch } = useContext(PokemonContext);
  const { pokemonsList, dispatchList } = useContext(PokemonsListContext);

  const Fetching = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: {
          limit,
          offset,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setResult(res?.data?.pokemons));
  };

  useEffect(() => {
    Fetching();

    return () => {};
  }, [offset]);

  useEffect(() => {
    result && setTotalPokemons(result.count);
    return () => {};
  }, [result]);

  useEffect(() => {
    dispatchList({ type: "LIMIT", state: { limit } });

    return () => {};
  }, []);

  const handleClickPrev = () => {
    dispatchList({ type: "LOWER_OFFSET", state: { limit } });
    setOffset((prev) => prev - 20);
  };

  const handleClickNext = () => {
    dispatchList({ type: "LOWER_OFFSET", state: { limit } });
    setOffset((prev) => prev + 20);
  };

  return (
    <Container className={`container-cards`}>
      <ul
        className={`list-cards dflex ${css`
          flex-wrap: wrap;
          justify-content: space-between;

          ${mq[0]} {
            padding-top: 1.2rem;
            padding-bottom: 1.2rem;
          }

          ${mq[3]} {
            padding-top: 3rem;
            padding-bottom: 3rem;
          }
        `}`}
      >
        {result &&
          result?.results?.map((item, key) => {
            return (
              <li
                key={key}
                className={`dflex flex-col pokemon-cards ${css`
                  align-items: center;
                  margin-top: 1rem;
                `}`}
              >
                <img src={item.image} alt="" />
                <p>{item.name}</p>
              </li>
            );
          })}
      </ul>

      <div
        className={`dflex flex-col wrapper-controllers ${css`
          align-items: flex-end;

          ${mq[0]} {
            padding: 1.2rem ${GSPACER.GUTTER[0]};
          }

          ${mq[3]} {
            padding: 3rem ${GSPACER.GUTTER[3]};
          }
        `}`}
      >
        {result && totalPokemons && (
          <p
            className={`${css`
              font-size: ${GFONT.SIZE0};
            `}`}
          >
            {totalPokemons} Pokemons
          </p>
        )}

        <div className={`dflex wrapper-buttons`}>
          <Button
            className={`btn-prev`}
            onClick={() => handleClickPrev()}
            disabled={offset <= 1}
          >
            Prev
          </Button>

          <Button
            className={`btn-next ${css`
              margin-left: 1rem;
            `}`}
            onClick={() => handleClickNext()}
          >
            Next
          </Button>
        </div>
      </div>
    </Container>
  );
};

export default PokemonListPage;
