import { css } from "@emotion/css";
import React from "react";
import { ContainerModal } from "../../styles/container";
import { mq } from "../../styles/globalStyle";
import { P } from "../../styles/text";

const PokemonModal = ({ data, img, modal }) => {
  return (
    <ContainerModal className={`container-modal`}>
      <div
        className={`modal dflex flex-col ${css`
          background-color: #c6c6c6;
          border-radius: 0.6rem;
          height: 100%;
          position: relative;
          margin-left: auto;
          margin-right: auto;
          width: 100%;

          ${mq[1]} {
            width: 75%;
          }

          ${mq[2]} {
            width: 50%;
          }

          ${mq[3]} {
            width: 25%;
          }
        `}`}
      >
        <button
          className={`btn-close ${css`
            background: transparent;
            cursor: pointer;
            margin: 1.5rem 1.5rem 1rem auto;

            ${mq[3]} {
              margin-top: 1.5rem;
              margin-right: 1.5rem;
              margin-bottom: 2rem;
            }

            ${mq[4]} {
              margin-top: 1.5rem;
              margin-right: 1.5rem;
              margin-bottom: 2rem;
            }
          `}`}
          onClick={() => modal((prev) => !prev)}
        >
          âœ•
        </button>

        <P
          className={`name ${css`
            font-size: var(--fs-s);
            font-weight: 600;
            letter-spacing: 0.2px;
            text-align: center;
            text-transform: uppercase;

            ${mq[3]} {
              font-size: var(--fs-l);
            }
          `}`}
        >
          {data.name}
        </P>

        <div
          className={`dflex content ${css`
            position: relative;
            flex-direction: column;
          `}`}
        >
          <div
            className={`wrapper-img ${css`
              position: relative;
              margin-left: auto;
              margin-right: auto;
            `}`}
          >
            <img
              className={`pokemon-img ${css``}`}
              src={img}
              alt="pokemon-img"
            />
          </div>

          {data && (
            <div
              className={`wrapper-information ${css`
                position: relative;
              `}`}
            >
              <div className="wrapper-abilities">
                {data.abilities.map((ability, key) => (
                  <P key={key} className={`ability`}>
                    {ability.ability.name}
                  </P>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </ContainerModal>
  );
};

export default PokemonModal;
