/* eslint-disable no-unused-vars */
/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useReducer, useState } from "react";
import { gqlQuery, gqlQueryAbilites } from "../PokemonQuerying";

import PokemonDetailsPage from "../PokemonDetailsPage";

import { Button } from "../../styles/button";
import {
  ContainerCards,
  Img,
  Li,
  LoadingText,
  P,
  Title,
  Ul,
  WrapperButton,
  WrapperLoadingScreen,
} from "./style";

const Fetching = (limit, offset, result) => {
  fetch("https://graphql-pokeapi.graphcdn.app/", {
    credentials: "omit",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      query: gqlQuery,
      variables: {
        limit,
        offset,
      },
    }),
    method: "POST",
  })
    .then((res) => res.json())
    .then((res) => result(res?.data?.pokemons));
};

const listReducer = (state, action) => {
  switch (action.type) {
    case "FETCH_POKEMON": {
      return { ...state };
    }

    case "POKEMON_DETAILS": {
      return { ...state };
    }

    default:
      return state;
  }
};

const initialState = {
  pokemonName: "ditto",
  pokemonImg: "",
  pokemonDetails: "",
  openModal: false,
  isLoading: false,
};

const PokemonListPage = () => {
  const [offset, setOffset] = useState(1);
  const [limit] = useState(20);
  const [result, setResult] = useState();

  const [pokemonName, setPokemonName] = useState("ditto");
  const [pokemonImg, setPokemonImg] = useState();
  const [pokemonDetails, setPokemonDetails] = useState();
  const [openModal, setOpenModal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const [state, dispatch] = useReducer(listReducer, initialState);

  /* const Fetching = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: {
          limit,
          offset,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setResult(res?.data?.pokemons));
  }; */

  const FetchingPokemonDetails = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQueryAbilites,
        variables: {
          pokemonName,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setPokemonDetails(res?.data?.pokemon));
  };

  useEffect(() => {
    Fetching(offset, limit, setResult);
    return () => {};
  }, [Fetching, offset]);

  useEffect(() => {
    FetchingPokemonDetails();
    return () => {};
  }, [pokemonName]);

  useEffect(() => {
    pokemonDetails && console.log("68", pokemonDetails);
    return () => {};
  }, [pokemonDetails]);

  const handleClickPokemon = (item) => {
    // extract pokemon name, and image
    setPokemonImg(item.image);
    setPokemonName(item.name);

    if (pokemonDetails) {
      setIsLoading(true);
      setIsLoading(false);
      setOpenModal((prev) => !prev);
    }
  };

  const handleClickPrev = () => {
    setOffset((prev) => prev - 20);
  };

  const handleClickNext = () => {
    setOffset((prev) => prev + 20);
  };

  return (
    <ContainerCards className="container-cards">
      {!openModal && !isLoading && (
        <>
          <div className="wrapper-title">
            <Title className="title">List of pokemons</Title>
          </div>
          <Ul className="list-cards">
            {result &&
              result?.results?.map((item, key) => {
                return (
                  <Li
                    // get pokemon information more details, show modal
                    onClick={() => handleClickPokemon(item)}
                    key={key}
                    className={`pokemon-cards`}
                  >
                    <Img className="" src={item.image} alt="img" />
                    <P>{item.name}</P>
                  </Li>
                );
              })}
          </Ul>

          <WrapperButton className="wrapper-buttons">
            <Button
              className="btn-prev"
              onClick={() => handleClickPrev()}
              disabled={offset <= 1}
            >
              <span>Prev</span>
            </Button>

            <Button className="btn-next" onClick={() => handleClickNext()}>
              <span>Next</span>
            </Button>
          </WrapperButton>
        </>
      )}

      {openModal && (
        <PokemonDetailsPage
          data={pokemonDetails}
          img={pokemonImg}
          modal={setOpenModal}
        />
      )}

      {isLoading && (
        <WrapperLoadingScreen>
          <LoadingText>Loading...</LoadingText>
        </WrapperLoadingScreen>
      )}
    </ContainerCards>
  );
};

export default PokemonListPage;
