/* eslint-disable no-unused-vars */
import React, { useContext, useEffect } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";

import { GFONT, MQ } from "../../styles/globalStyle";
import { css } from "@emotion/css";

const mq = MQ.map((mq) => `@media (min-width: ${mq}px)`);

const Navbar = () => {
  const { pokemons } = useContext(PokemonContext);

  return (
    <>
      <nav
        className={`navbar ${css`
          padding: 0.7rem 1.4rem;
          text-align: center;
        `}`}
      >
        <h1
          className={`header ${css`
            ${mq[0]} {
              padding-top: 0.2rem;
              font-size: ${GFONT.SIZE1};
            }
          `}`}
        >
          Pokemon List Page
        </h1>
        <p
          className={`title ${css`
            ${mq[0]} {
              padding-top: 0.2rem;
              font-size: ${GFONT.SIZE2};
            }

            ${mq[2]} {
              padding-top: 0.4rem;
            }
          `}`}
        >
          Currently you have {pokemons.length} pokemons in possession.{" "}
        </p>
      </nav>

      {pokemons.length && (
        <ul className={`wrapper-list-item-pokemon ${css``} `}>
          {pokemons.map((item, i) => {
            return (
              <p className={`list-item-pokemon `} key={i}>
                {item.name}
              </p>
            );
          })}
        </ul>
      )}
    </>
  );
};

export default Navbar;
