import styled from "@emotion/styled";
import { GCOLOR, GFONT, GSPACER, mq } from "../../styles/globalStyle";

export const ButtonClose = styled.div`
  background: transparent;
  cursor: pointer;
  margin: 1.5rem 1.5rem 1rem auto;

  ${mq[2]} {
    margin-top: 1.25rem;
    margin-right: 1.25rem;
    margin-bottom: 1rem;
  }

  ${mq[3]} {
    margin-top: 1.1rem;
    margin-right: 1.25rem;
  }
`;

export const ContainerModal = styled.div`
  bottom: 0;
  left: 0;
  padding-left: ${GSPACER.GUTTER[3]};
  padding-right: ${GSPACER.GUTTER[3]};
  position: absolute;
  right: 0;
  top: 0;
  z-index: 1;
`;

export const Modal = styled.div`
  background-color: #c6c6c6;
  border-radius: 0.6rem;
  display: flex;
  flex-direction: column;
  height: 100%;
  margin-left: auto;
  margin-right: auto;
  position: relative;
  width: 100%;

  ${mq[1]} {
    width: 75%;
  }

  ${mq[2]} {
    width: 50%;
  }

  ${mq[3]} {
    width: 25%;
  }
`;

export const Li = styled.li`
  position: relative;
  padding-left: 1rem;
  min-width: 50%;

  &:before {
    content: "✓";
    position: absolute;
    left: 0;
  }
`;

export const Name = styled.p`
  color: ${GCOLOR.GRAY}
   font-size: ${GFONT.SIZE[0]};\
  font-weight: 600;
  letter-spacing: 0.2px;
  text-align: center;
  text-transform: uppercase;

  ${mq[3]} {
    font-size: var(--fs-l);
  }
`;

export const Ul = styled.ul`
  display: flex;
  flex-wrap: wrap;
`;

export const WrapperInformation = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  padding-left: ${GSPACER.GUTTER[0]};
  padding-right: ${GSPACER.GUTTER[0]};
  position: relative;

  ${mq[3]} {
    padding-left: ${GSPACER.GUTTER[2]};
    padding-right: ${GSPACER.GUTTER[2]};
  }
`;

export const SmallNote = styled.p`
  color: ${GCOLOR.GRAY};

  ${mq[3]} {
    font-size: ${GFONT.SIZE[0]};
  }
`;
