import React, { useEffect, useState } from "react";
import { css } from "@emotion/css";
import {
  ButtonClose,
  ContainerModal,
  Li,
  Modal,
  Name,
  Ul,
  WrapperInformation,
} from "./style";

const PokemonModal = ({ data, img, modal }) => {
  const [showLimit, setShowLimit] = useState(10);

  return (
    <ContainerModal className="container-modal">
      <Modal className="modal">
        <ButtonClose
          className="btn-close"
          onClick={() => modal((prev) => !prev)}
        >
          âœ•
        </ButtonClose>

        <Name className="name">{data.name}</Name>

        <div
          className={`dflex content ${css`
            position: relative;
            flex-direction: column;
          `}`}
        >
          <div
            className={`wrapper-img ${css`
              position: relative;
              margin-left: auto;
              margin-right: auto;
            `}`}
          >
            <img className="pokemon-img" src={img} alt="pokemon-img" />
          </div>

          {data && (
            <WrapperInformation className="wrapper-information">
              <div className="information-abilities">
                Abilities:
                <Ul>
                  {data.abilities.map((ability, key) => (
                    <Li key={key} className="list-ability">
                      {ability.ability.name}
                    </Li>
                  ))}
                </Ul>
              </div>
              <div className="information-moves">
                Moves:
                <Ul>
                  {data.moves.splice(0, showLimit).map((move, key) => (
                    <Li key={key} className="list-move">
                      {move.move.name}
                    </Li>
                  ))}
                </Ul>
                ...{data.moves.length}
              </div>
              <div className="information-types">
                Types:
                <Ul>
                  {data.types.map((type, key) => (
                    <Li key={key} className="list-type">
                      {type.type.name}
                    </Li>
                  ))}
                </Ul>
              </div>
            </WrapperInformation>
          )}
        </div>
      </Modal>
    </ContainerModal>
  );
};

export default PokemonModal;
