import React, { useState } from "react";
import {
  ButtonBack,
  Container,
  DetailPage,
  Dice,
  Img,
  Li,
  Name,
  SmallNote,
  Title,
  ULAbilities,
  ULMoves,
  ULTypes,
} from "./style";
import { IconBack, IconPokeBall } from "../../assets/IconPack";

const PokemonDetailsPage = ({ data, img, modal }) => {
  const [limitMoves] = useState(15);
  const [diceResult, setDiceResult] = useState(0);

  const randomNumber = () => {
    const result = Math.random().toFixed(2);

    setDiceResult(result < 0.8 ? "0" : "1");
    console.log(result);
  };

  return (
    <DetailPage className="pokemon-detail-page">
      <ButtonBack className="btn-back" onClick={() => modal((prev) => !prev)}>
        <IconBack />
      </ButtonBack>

      <Name className="name">{data.name}</Name>

      <Container className="container">
        <Img className="pokemon-img" src={img} alt="pokemon-img" />

        {data && (
          <>
            <ULAbilities>
              <Title>Abilities:</Title>
              {data.abilities.map((ability, key) => (
                <Li key={key} className="list-ability">
                  {ability.ability.name}
                </Li>
              ))}
            </ULAbilities>

            <ULMoves>
              <Title>Moves:</Title>
              {data.moves.map(
                (move, key) =>
                  key < limitMoves && (
                    <Li key={key} className="list-move">
                      {move.move.name}
                    </Li>
                  )
              )}
              <SmallNote>only {limitMoves} moves displayed</SmallNote>
            </ULMoves>

            <ULTypes>
              <Title>Types:</Title>
              {data.types.map((type, index) => (
                <Li key={index} className="list-type">
                  {type.type.name}
                </Li>
              ))}
            </ULTypes>
          </>
        )}
        {diceResult}
        <IconPokeBall />
        <Dice onClick={() => randomNumber()}>Catch'em</Dice>
      </Container>
    </DetailPage>
  );
};

export default PokemonDetailsPage;
