/* eslint-disable no-unused-vars */
import React, { useContext, useEffect } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";

import { REDUCER } from "../../constant/constantReducer";
import { GFONT, GSPACER, mq } from "../../styles/globalStyle";
import { css } from "@emotion/css";
import { ContainerTitle } from "./style";

const { RELEASE_POKEMON } = REDUCER;

const Titlebar = () => {
  const { pokemons, dispatch } = useContext(PokemonContext);

  const releasePokemon = (id) => {
    dispatch({ type: RELEASE_POKEMON, id });
  };

  return (
    <ContainerTitle className="container-title">
      <h1 className={`header ${css``}`}>Pokemon List Page</h1>

      <p
        className={`title ${css`
          ${mq[0]} {
            padding-top: 0.2rem;
            font-size: ${GFONT.SIZE[1]};
          }

          ${mq[3]} {
            padding-top: 0.4rem;
            font-size: ${GFONT.SIZE[2]};
          }
        `}`}
      >
        You
        {pokemons.length > 0
          ? ` have ${pokemons.length} pokemon${
              pokemons.length > 1 ? "s" : ""
            } in your possessions`
          : ` don't have any pokemons in your inventory`}
      </p>

      {pokemons.length > 0 && (
        <ul
          className={`wrapper-list-item-pokemon dflex flex-col ${css`
            ${mq[0]} {
              padding: 1rem ${GSPACER.GUTTER[0]};
            }

            ${mq[3]} {
              padding: 1.2rem ${GSPACER.GUTTER[4]};
            }
          `} `}
        >
          {pokemons.map((uniquepokemon, i) => {
            return (
              <p
                className={`list-item-pokemon `}
                key={i}
                onClick={() => releasePokemon(uniquepokemon.id)}
              >
                {uniquepokemon.name}
              </p>
            );
          })}
        </ul>
      )}
    </ContainerTitle>
  );
};

export default Titlebar;
