import styled from "@emotion/styled";
import { GSPACER, mq } from "../../styles/globalStyle";

export const Container = styled.div`
  position: relative;

  ${mq[0]} {
    padding: 1rem ${GSPACER.GUTTER[0]};
  }

  ${mq[3]} {
    margin-left: auto;
    margin-right: auto;
    max-width: var(--screen-max-width);
    padding: 1rem ${GSPACER.GUTTER[3]};
  }
`;

export const H1 = styled.h1`
  position: relative;
  text-align: center;

  ${mq[0]} {
    padding-top: 0.7rem;
    padding-bottom: 0.7rem;
  }

  ${mq[3]} {
    padding-top: 1rem;
    padding-bottom: 1rem;
  }
`;

export const Content = styled.div`
  display: flex;
  justify-content: space-between;
`;

export const WrapperAbilities = styled.div`
  border: 1px solid blue;
`;
export const WrapperMoves = styled.div``;
export const WrapperTypes = styled.div``;
