import React, { useContext, useState } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";
import { REDUCER } from "../../constant/constantReducer";
import { IconX } from "../../assets/IconPack";

import { css } from "@emotion/css";
import { GCOLOR, GFONT, mq, GSPACER } from "../../styles/globalStyle";
import { Button } from "../../styles/button";
import { ButtonClear, Input, WrapperInput } from "./style";

const { ADD_POKEMON } = REDUCER;

const PokemonForm = () => {
  const { dispatch } = useContext(PokemonContext);
  const [name, setName] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch({ type: ADD_POKEMON, pokemon: { name } });
    setName("");
  };

  const handleClear = () => {
    setName("");
  };

  return (
    <form
      className={`form ${css`
        ${mq[0]} {
          padding-left: ${GSPACER.GUTTER[0]};
          padding-right: ${GSPACER.GUTTER[0]};
        }

        ${mq[3]} {
          margin: 0 auto;
          max-width: var(--screen-max-width);
          padding-left: ${GSPACER.GUTTER[3]};
          padding-right: ${GSPACER.GUTTER[3]};
        }
      `}`}
      onSubmit={handleSubmit}
    >
      <WrapperInput className="wrapper-input">
        <Input
          className="input-pokemon-name"
          onChange={(e) => setName(e.target.value)}
          placeholder="Named your Pokemon"
          required
          type="text"
          value={name}
        />

        {name && (
          <ButtonClear onClick={() => handleClear()} className="wrapper-icon">
            <IconX />
          </ButtonClear>
        )}
      </WrapperInput>

      <Button className={`btn-form`} type="submit">
        Submit
      </Button>
    </form>
  );
};

export default PokemonForm;
