/* eslint-disable no-unused-vars */
import React, { useContext, useEffect } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";

import { GFONT, GMQ, GSPACER } from "../../styles/globalStyle";
import { css } from "@emotion/css";

const mq = GMQ.map((mq) => `@media (min-width: ${mq}px)`);

const Titlebar = () => {
  const { pokemons } = useContext(PokemonContext);

  const releasePokemon = () => {};

  return (
    <div className={``}>
      <div
        className={`titlebar dflex flex-col ${css`
          text-align: center;

          ${mq[0]} {
            padding: 0.7rem ${GSPACER.GUTTER[0]};
          }

          ${mq[3]} {
            padding: 0.8rem ${GSPACER.GUTTER[4]};
          }
        `}`}
      >
        <h1
          className={`header ${css`
            ${mq[0]} {
              font-size: ${GFONT.SIZE3};
            }

            ${mq[3]} {
              font-size: ${GFONT.SIZE6};
            }
          `}`}
        >
          Pokemon List Page
        </h1>
        <p
          className={`title ${css`
            ${mq[0]} {
              padding-top: 0.2rem;
              font-size: ${GFONT.SIZE2};
            }

            ${mq[3]} {
              padding-top: 0.4rem;
              font-size: ${GFONT.SIZE3};
            }
          `}`}
        >
          Currently you have {pokemons.length} pokemons in possession.{" "}
        </p>
      </div>

      {pokemons.length && (
        <ul
          className={`wrapper-list-item-pokemon dflex flex-col ${css`
            ${mq[0]} {
              padding: 0.7rem ${GSPACER.GUTTER[0]};
            }

            ${mq[3]} {
              padding: 0.8rem ${GSPACER.GUTTER[4]};
            }
          `} `}
        >
          {pokemons.map((item, i) => {
            return (
              <p
                className={`list-item-pokemon `}
                key={i}
                onClick={releasePokemon(item.id)}
              >
                {item.name}
              </p>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default Titlebar;
