/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useReducer, useState } from "react";
import { gqlQuery, gqlQueryAbilites } from "../PokemonQuerying";

import PokemonDetailsPage from "../PokemonDetailsPage";

import { Button } from "../../styles/button";
import {
  ContainerCards,
  Img,
  Li,
  LoadingText,
  P,
  Title,
  Ul,
  WrapperButton,
  WrapperLoadingScreen,
} from "./style";

export const LISTACTION = {
  FETCH_POKEMON: "FETCH POKEMON",
  FETCH_DONE: "FETCH DONE",
  RAISE_OFFSET: "RAISE OFFSET",
  REDUCE_OFFSET: "REDUCE OFFSET",
  CLOSE: "CLOSE",
};

const listReducer = (state, action) => {
  switch (action.type) {
    case LISTACTION.FETCH_POKEMON: {
      return {
        ...state,
        isLoading: true,
        pokemonImg: action.payload.image,
        pokemonName: action.payload.name,
      };
    }

    case LISTACTION.FETCH_DONE: {
      return {
        ...state,
        isLoading: false,
        openModal: !state.openModal,
      };
    }

    case LISTACTION.RAISE_OFFSET: {
      return {
        ...state,
        offset: (state.offset += 20),
      };
    }

    case LISTACTION.REDUCE_OFFSET: {
      return {
        ...state,
        offset: state.offset <= 1 ? state.offset : (state.offset -= 20),
      };
    }

    case LISTACTION.CLOSE: {
      return {
        ...state,
        openModal: !state.openModal,
      };
    }

    default:
      return state;
  }
};

const initialState = {
  pokemonName: "ditto",
  pokemonImg: "",
  pokemonDetails: "",
  openModal: false,
  isLoading: false,
  offset: 1,
  limit: 20,
};

const PokemonListPage = () => {
  const [offset] = useState(1);
  const [limit] = useState(20);
  const [pokemonName] = useState("ditto");

  const [result, setResult] = useState();
  const [pokemonDetails, setPokemonDetails] = useState();

  const [state, dispatch] = useReducer(listReducer, initialState);

  const Fetching = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: {
          limit,
          offset,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setResult(res?.data?.pokemons));
  };

  const FetchingPokemonDetails = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQueryAbilites,
        variables: {
          pokemonName,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setPokemonDetails(res?.data?.pokemon));
  };

  useEffect(() => {
    Fetching();
    return () => {};
  }, [Fetching, offset]);

  useEffect(() => {
    FetchingPokemonDetails();
    return () => {};
  }, [pokemonName]);

  const handleClickPokemon = (item) => {
    // extract pokemon name, and image
    dispatch({
      type: LISTACTION.FETCH_POKEMON,
      payload: { image: item.image, name: item.name },
    });

    if (pokemonDetails) {
      dispatch({
        type: LISTACTION.FETCH_DONE,
      });
    }
  };

  const handleClickPrev = () => {
    dispatch({
      type: LISTACTION.REDUCE_OFFSET,
    });
  };

  const handleClickNext = () => {
    dispatch({
      type: LISTACTION.RAISE_OFFSET,
    });
  };

  return (
    <ContainerCards className="container-cards">
      {!state.openModal && !state.isLoading && (
        <>
          <div className="wrapper-title">
            <Title className="title">List of pokemons</Title>
          </div>
          <Ul className="list-cards">
            {result &&
              result?.results?.map((item, key) => {
                return (
                  <Li
                    // get pokemon information more details, show modal
                    onClick={() => handleClickPokemon(item)}
                    key={key}
                    className={`pokemon-cards`}
                  >
                    <Img className="" src={item.image} alt="img" />
                    <P>{item.name}</P>
                  </Li>
                );
              })}
          </Ul>

          <WrapperButton className="wrapper-buttons">
            <Button
              className="btn-prev"
              onClick={() => handleClickPrev()}
              disabled={state.offset <= 1}
            >
              <span>Prev</span>
            </Button>

            <Button className="btn-next" onClick={() => handleClickNext()}>
              <span>Next</span>
            </Button>
          </WrapperButton>
        </>
      )}

      {state.isLoading && (
        <WrapperLoadingScreen>
          <LoadingText>Loading...</LoadingText>
        </WrapperLoadingScreen>
      )}

      {state.openModal && (
        <PokemonDetailsPage
          data={pokemonDetails}
          img={state.pokemonImg}
          dispatch={dispatch}
        />
      )}
    </ContainerCards>
  );
};

export default PokemonListPage;
