import React, { createContext, useReducer, useEffect } from "react";
import pokemonReducer from "../reducers/pokemonReducer";
import pokemonListReducer from "../reducers/pokemonListReducer";

export const PokemonContext = createContext();
export const PokemonsListContext = createContext();

const PokemonContextProvider = (props) => {
  const [pokemonsList, dispatchList] = useReducer();

  const [pokemons, dispatch] = useReducer(pokemonReducer, [], () => {
    const localdata = localStorage.getItem("pokemons");
    return localdata ? JSON.parse(localdata) : [];
  });

  useEffect(() => {
    localStorage.setItem("pokemons", JSON.stringify(pokemons));
  }, [pokemons]);

  return (
    <PokemonsListContext.Provider value={{ pokemonsList, dispatchList }}>
      <PokemonContext.Provider value={{ pokemons, dispatch }}>
        {props.children}
      </PokemonContext.Provider>
    </PokemonsListContext.Provider>
  );
};

export default PokemonContextProvider;
