import styled from "@emotion/styled";
import { GSPACER, mq } from "./globalStyle";

export const ContainerTitle = styled.div`
  display: flex;
  flex-direction: column;
  position: relative;
  text-align: center;
  width: 100%;

  ${mq[0]} {
    padding: 0.7rem ${GSPACER.GUTTER[0]};
  }

  ${mq[3]} {
    margin: 0 auto;
    max-width: var(--screen-max-width);
    padding: 1rem ${GSPACER.GUTTER[3]};
  }
`;

export const ContainerModal = styled.div`
  bottom: 0;
  left: 0;
  padding-left: ${GSPACER.GUTTER[3]};
  padding-right: ${GSPACER.GUTTER[3]};
  position: absolute;
  right: 0;
  top: 0;
  z-index: 1;
`;
