import styled from "@emotion/styled";
import { GCOLOR, GFONT, GSPACER, mq } from "../../styles/globalStyle";

export const ContainerCards = styled.div`
  padding-left: ${GSPACER.GUTTER[0]};
  padding-right: ${GSPACER.GUTTER[0]};
  position: relative;
  width: 100%;

  ${mq[3]} {
    margin: 0 auto;
    max-width: var(--screen-max-width);
    padding-left: ${GSPACER.GUTTER[3]};
    padding-right: ${GSPACER.GUTTER[3]};
  }
`;

export const Img = styled.img`
  ${mq[3]} {
    transform: scale(0.9);
  }
`;

export const Ul = styled.ul`
  display: flex;
  flex-wrap: wrap;
  position: relative;
  padding-top: 0.5rem;
  padding-bottom: 0.5rem;
  gap: 0.3rem;

  ${mq[3]} {
    padding-top: 2rem;
    padding-bottom: 2rem;
    gap: 0.4rem;
  }
`;

export const Li = styled.li`
  background-color: ${GCOLOR.GRAY24};
  align-items: center;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  transform: scale(0.95);
`;

export const P = styled.p`
  padding-top: 0.2rem;
  padding-bottom: 0.2rem;
  font-size: ${GFONT.SIZE[2]};

  ${mq[3]} {
    font-size: ${GFONT.SIZE[1]};
  }
`;

export const WrapperButton = styled.div`
  display: flex;
  justify-content: flex-end;
  position: relative;
  padding-top: 1.2rem;
  padding-bottom: 1.2rem;

  ${mq[3]} {
    margin: 0 auto;
    max-width: var(--screen-max-width);
    padding-bottom: 2.25rem;
    padding-top: 2.25rem;
  }
`;

export const WrapperLoadingScreen = styled.div`
  align-items: center;
  bottom: 0;
  display: flex;
  justify-content: center;
  left: 0;
  min-height: 100vh;
  position: absolute;
  right: 0;
  top: 0;
  width: 100%;
`;

export const LoadingText = styled.p`
  color: ${GCOLOR.BLUEISH};
  font-size: ${GFONT.SIZE[7]};
`;

export const Title = styled.p`
  font-size: ${GFONT.SIZE[7]};
  padding-top: 1.2rem;
  padding-bottom: 1.2rem;

  ${mq[3]} {
    max-width: var(--screen-max-width);
    padding-top: 1.5rem;
    padding-bottom: 1em;
  }
`;
