import styled from "@emotion/styled";
import { GCOLOR, GFONT, GSPACER, mq } from "../../styles/globalStyle";

export const DetailPage = styled.div`
  bottom: 0;
  left: 0;
  min-height: 100vh;
  padding-left: ${GSPACER.GUTTER[0]};
  padding-right: ${GSPACER.GUTTER[0]};
  position: absolute;
  right: 0;
  top: 0;
  width: 100%;
  z-index: 1;

  ${mq[3]} {
    padding: 2rem ${GSPACER.GUTTER[3]};
  }
`;

export const ButtonBack = styled.button`
  align-items center;
  background: transparent;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  height: 60px;
  justify-content: center;
  position: relative;
  width: 60px;
  margin-bottom: 0.5rem;

  ${mq[3]} {
    height: 80px;
    width: 80px;
    margin-bottom: 1rem;
  }
`;

export const Container = styled.div`
  position: relative;
  width: 100%;

  ${mq[3]} {
    margin: 0 auto;
    max-width: var(--screen-max-width);
  }
`;

export const Li = styled.li`
  position: relative;
  padding-left: 1rem;
  min-width: 50%;

  &:before {
    content: "✓";
    position: absolute;
    left: 0;
  }
`;

export const Name = styled.p`
  color: ${GCOLOR.TERTIER};
  font-size: ${GFONT.SIZE[3]};
  font-weight: 600;
  letter-spacing: 0.5px;
  text-transform: uppercase;

  ${mq[3]} {
    font-size: ${GFONT.SIZE[5]};
  }
`;

export const Ul = styled.ul`
  display: flex;
  flex-wrap: wrap;
`;

export const WrapperInformation = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  padding-left: ${GSPACER.GUTTER[0]};
  padding-right: ${GSPACER.GUTTER[0]};
  position: relative;

  ${mq[3]} {
    font-size: ${GFONT.SIZE[1]};
    padding-left: ${GSPACER.GUTTER[2]};
    padding-right: ${GSPACER.GUTTER[2]};
  }
`;

export const SmallNote = styled.p`
  color: ${GCOLOR.GRAY};

  ${mq[3]} {
    font-size: ${GFONT.SIZE[0]};
  }
`;
