/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unused-vars */
import React, { useContext, useEffect, useState } from "react";
import { gqlQuery, gqlQueryAbilites } from "../Querying";

import { css } from "@emotion/css";
import { mq } from "../../styles/globalStyle";
import { Button } from "../../styles/button";
import { Container } from "../../styles/container";

const PokemonListPage = () => {
  const [offset, setOffset] = useState(1);
  const [limit, setLimit] = useState(20);
  const [result, setResult] = useState();

  const [pokemonName, setPokemonName] = useState("ditto");
  const [pokemonDetails, setPokemonDetails] = useState();
  const [openModal, setOpenModal] = useState(false);

  const Fetching = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: {
          limit,
          offset,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setResult(res?.data?.pokemons));
  };

  const FetchingPokemonDetails = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQueryAbilites,
        variables: {
          pokemonName,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setPokemonDetails(res?.data?.pokemon));
  };

  useEffect(() => {
    Fetching();

    return () => {};
  }, [offset]);

  useEffect(() => {
    FetchingPokemonDetails();

    return () => {};
  }, [pokemonName]);

  useEffect(() => {
    pokemonDetails && console.log(pokemonDetails.name);
    return () => {};
  }, [pokemonDetails]);

  // setter getter pokemonName when user clicks
  const handleClickPokemon = (name) => {
    setPokemonName(name);
    setOpenModal((prev) => !prev);
  };

  const handleClickPrev = () => {
    setOffset((prev) => prev - 20);
  };

  const handleClickNext = () => {
    setOffset((prev) => prev + 20);
  };

  return (
    <Container className={`container-cards`}>
      <ul
        className={`list-cards dflex ${css`
          flex-wrap: wrap;
          justify-content: space-between;

          ${mq[0]} {
            padding-top: 1.2rem;
            padding-bottom: 1.2rem;
          }

          ${mq[3]} {
            padding-top: 2.5rem;
            padding-bottom: 2.5rem;
          }
        `}`}
      >
        {result &&
          result?.results?.map((item, key) => {
            return (
              <li
                // get pokemon name
                onClick={() => handleClickPokemon(item.name)}
                key={key}
                className={`dflex flex-col pokemon-cards ${css`
                  align-items: center;
                  margin-top: 1rem;
                `}`}
              >
                <img src={item.image} alt="" />
                <p>{item.name}</p>
              </li>
            );
          })}
      </ul>

      {openModal && (
        <div
          className={`container-modal ${css`
            position: absolute;
            background-color: #fefefe;
            width: 100%;
            height: 100%;
            top: 0;
            z-index: 1;
          `}`}
        >
          <span
            className={`dflex ${css`
              justify-content: flex-end;
              position: relative;
              margin-right: 1rem;
            `}`}
            onClick={() => setOpenModal((prev) => !prev)}
          >
            X
          </span>
          {pokemonDetails && (
            <>
              <p
                className={`name ${css`
                  color: #000000;
                `}`}
              >
                {pokemonDetails.name}
              </p>
            </>
          )}
        </div>
      )}

      <div
        className={`dflex wrapper-buttons ${css`
          justify-content: flex-end;

          ${mq[0]} {
            padding-top: 1.2rem;
            padding-bottom: 1.2rem;
          }

          ${mq[3]} {
            padding-top: 2.5rem;
            padding-bottom: 2.5rem;
          }
        `}`}
      >
        <Button
          className={`btn-prev`}
          onClick={() => handleClickPrev()}
          disabled={offset <= 1}
        >
          Prev
        </Button>

        <Button
          className={`btn-next ${css`
            margin-left: 1rem;
          `}`}
          onClick={() => handleClickNext()}
        >
          Next
        </Button>
      </div>
    </Container>
  );
};

export default PokemonListPage;
