import React, { useContext, useState } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";
import { REDUCER } from "../../constant/constantReducer";
import { IconX } from "../../assets/IconPack";

import { css } from "@emotion/css";
import { GCOLOR, GFONT, mq, GSPACER } from "../../styles/globalStyle";
import { Button } from "../../styles/button";

const { ADD_POKEMON } = REDUCER;

const PokemonForm = () => {
  const { dispatch } = useContext(PokemonContext);
  const [name, setName] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch({ type: ADD_POKEMON, pokemon: { name } });
    setName("");
  };

  const handleClear = () => {
    setName("");
  };

  return (
    <form
      className={`form ${css`
        ${mq[0]} {
          padding: 1.2rem ${GSPACER.GUTTER[0]};
        }

        ${mq[3]} {
          padding: 2.5rem ${GSPACER.GUTTER[3]};
        }
      `}`}
      onSubmit={handleSubmit}
    >
      <div className={`wrapper-input dflex ${css``}`}>
        <input
          className={`form-input ${css`
            background-color: ${GCOLOR.SECONDARY};
            border-radius: calc(0.25em + 0.15vw);
            border: 1px solid ${GCOLOR.TERTIER};
            color: ${GCOLOR.TERTIER};
            font-size: ${GFONT.SIZE1};
            padding-bottom: 0.6rem;
            padding-top: 0.6rem;
            width: 100%;

            ${mq[0]} {
              padding-left: ${GSPACER.GUTTER[0]};
              padding-right: ${GSPACER.GUTTER[0]};
            }

            ${mq[3]} {
              padding-left: ${GSPACER.GUTTER[3]};
              padding-right: ${GSPACER.GUTTER[3]};
            }
          `} `}
          onChange={(e) => setName(e.target.value)}
          placeholder="Named your Pokemon"
          required
          type="text"
          value={name}
        />

        {name && (
          <div
            onClick={() => handleClear()}
            className={`wrapper-icon ${css`
              cursor: pointer;
              display: flex;
              position: absolute;
              right: 0;
              top: 50%;
              transform: translateY(-50%) scale(0.825);

              ${mq[0]} {
                padding-right: ${GSPACER.GUTTER[0]};
              }

              ${mq[3]} {
                padding-right: ${GSPACER.GUTTER[3]};
              }
            `}`}
          >
            <IconX />
          </div>
        )}
      </div>

      <Button className={`btn-form`} type="submit">
        Submit
      </Button>
    </form>
  );
};

export default PokemonForm;
