/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unused-vars */
import React, { useContext, useEffect, useState } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";

import { css } from "@emotion/css";
import { GFONT, GSPACER, mq } from "../../styles/globalStyle";

const gqlQuery = `query pokemons($limit: Int, $offset: Int) {
  pokemons(limit: $limit, offset: $offset) {
    count
    next
    previous
    status
    message
    results {
      url
      name
      image
    }
  }
}`;

const gqlVariables = {
  limit: 10,
  offset: 1,
};

const PokemonListPage = () => {
  const [result, setResult] = useState();
  const { pokemons } = useContext(PokemonContext);

  const Fetching = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: gqlVariables,
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setResult(res));
  };

  useEffect(() => {
    Fetching();
    return () => {};
  }, []);

  useEffect(() => {
    result && console.log(result?.data?.pokemons);
    return () => {};
  }, [result]);

  return (
    <div className={`container-cards ${css``}`}>
      <ul
        className={`list-cards dflex ${css`
          flex-wrap: wrap;
          justify-content: space-between;

          ${mq[0]} {
            padding: 1.2rem ${GSPACER.GUTTER[0]};
          }

          ${mq[3]} {
            padding: 3rem ${GSPACER.GUTTER[3]};
          }
        `}`}
      >
        {result &&
          result?.data?.pokemons?.results?.map((item, key) => {
            return (
              <li
                key={key}
                className={`dflex flex-col pokemon-cards ${css`
                  align-items: center;

                  ${mq[0]} {
                    margin-top: 0.4rem;
                  }

                  ${mq[2]} {
                    margin-top: 0;
                  }
                `}`}
              >
                <img src={item.image} alt="" />
                <p>{item.name}</p>
              </li>
            );
          })}
      </ul>
    </div>
  );
};

export default PokemonListPage;
