import React, { useEffect, useState } from "react";
import { css } from "@emotion/css";
import { ButtonClose, ContainerModal, Li, Modal, Name, Ul } from "./style";

const PokemonModal = ({ data, img, modal }) => {
  const size = 3;
  const [modifiedTypes, setModifiedTypes] = useState();

  useEffect(() => {
    data.types.map((type, key) => setModifiedTypes(type.slice(0, size)));

    return () => {};
  }, [data]);

  useEffect(() => {
    console.log(modifiedTypes);
    return () => {};
  }, [modifiedTypes]);

  return (
    <ContainerModal className="container-modal">
      <Modal className="modal">
        <ButtonClose
          className="btn-close"
          onClick={() => modal((prev) => !prev)}
        >
          âœ•
        </ButtonClose>

        <Name className="name">{data.name}</Name>

        <div
          className={`dflex content ${css`
            position: relative;
            flex-direction: column;
          `}`}
        >
          <div
            className={`wrapper-img ${css`
              position: relative;
              margin-left: auto;
              margin-right: auto;
            `}`}
          >
            <img className="pokemon-img" src={img} alt="pokemon-img" />
          </div>

          {data && (
            <div className="wrapper-information">
              Abilities:
              <Ul className="information-abilities">
                {data.abilities.map((ability, key) => (
                  <Li key={key} className="ability">
                    {ability.ability.name}
                  </Li>
                ))}
              </Ul>
              Moves:
              <Ul className="information-moves">
                {data.moves.map((move, key) => (
                  <Li key={key} className="move">
                    {move.move.name}
                  </Li>
                ))}
              </Ul>
              Types:
              <Ul className="information-types">
                {data.types.map((type, key) => (
                  <Li key={key} className="type">
                    {type.type.name}
                  </Li>
                ))}
              </Ul>
            </div>
          )}
        </div>
      </Modal>
    </ContainerModal>
  );
};

export default PokemonModal;
