import styled from "@emotion/styled";
import { mq } from "../../styles/globalStyle";

export const Modal = styled.div`
  background-color: #c6c6c6;
  border-radius: 0.6rem;
  display: flex;
  flex-direction: column;
  height: 100%;
  margin-left: auto;
  margin-right: auto;
  position: relative;
  width: 100%;

  ${mq[1]} {
    width: 75%;
  }

  ${mq[2]} {
    width: 50%;
  }

  ${mq[3]} {
    width: 25%;
  }
`;

export const ButtonClose = styled.div`
  background: transparent;
  cursor: pointer;
  margin: 1.5rem 1.5rem 1rem auto;

  ${mq[2]} {
    margin-top: 1.25rem;
    margin-right: 1.25rem;
    margin-bottom: 2rem;
  }

  ${mq[3]} {
    margin-top: 1.5rem;
    margin-right: 1.5rem;
  }
`;
