import React, { useContext, useState } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";
import { REDUCER } from "../../constant/constantReducer";
import { IconX } from "../../assets/IconPack";
import { css } from "@emotion/css";
import { GCOLOR, GFONT, mq, GSPACER } from "../../styles/globalStyle";

const { ADD_POKEMON } = REDUCER;

const PokemonForm = () => {
  const { dispatch } = useContext(PokemonContext);
  const [name, setName] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch({ type: ADD_POKEMON, pokemon: { name } });
    setName("");
  };

  return (
    <form
      className={`form ${css`
        ${mq[0]} {
          padding: 1.2rem ${GSPACER.GUTTER[0]};
        }

        ${mq[3]} {
          padding: 3rem ${GSPACER.GUTTER[3]};
        }
      `}`}
      onSubmit={handleSubmit}
    >
      <div className={`wrapper-input dflex ${css``}`}>
        <input
          className={`form-input ${css`
            background-color: ${GCOLOR.SECONDARY};
            border: 1px solid ${GCOLOR.TERTIER};
            border-radius: calc(0.25em + 0.15vw);
            color: ${GCOLOR.TERTIER};
            width: 100%;

            ${mq[0]} {
              font-size: ${GFONT.SIZE0};
              padding: 0.5rem ${GSPACER.GUTTER[0]};
            }

            ${mq[3]} {
              font-size: ${GFONT.SIZE1};
              padding: 0.5rem ${GSPACER.GUTTER[3]};
            }
          `} `}
          onChange={(e) => setName(e.target.value)}
          placeholder="Named your Pokemon"
          required
          type="text"
          value={name}
        />
        <IconX
          className={`position: absolute;
          top: 50%;
          translate: transformY(-50%);`}
        />
      </div>

      <button
        className={`form-btn ${css`
          background-color: ${GCOLOR.SECONDARY};
          border: 1px solid ${GCOLOR.TERTIER};
          border-radius: calc(0.25em + 0.15vw);
          color: ${GCOLOR.TERTIER};

          ${mq[0]} {
            font-size: ${GFONT.SIZE0};
            margin-top: 0.25rem;
            padding: 0.5rem ${GSPACER.GUTTER[0]};
          }

          ${mq[3]} {
            font-size: ${GFONT.SIZE1};
            margin-top: 0.4rem;
            padding: 0.5rem ${GSPACER.GUTTER[3]};
          }
        `}`}
        type="submit"
      >
        Submit
      </button>
    </form>
  );
};

export default PokemonForm;
