/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unused-vars */
import React, { useContext, useEffect, useState } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";

import { css } from "@emotion/css";
import { GFONT } from "../../styles/globalStyle";

const gqlQuery = `query pokemons($limit: Int, $offset: Int) {
  pokemons(limit: $limit, offset: $offset) {
    count
    next
    previous
    status
    message
    results {
      url
      name
      image
    }
  }
}`;

const gqlVariables = {
  limit: 10,
  offset: 1,
};

const PokemonListPage = () => {
  const [result, setResult] = useState();
  const { pokemons } = useContext(PokemonContext);

  const Fetching = async () => {
    const res = await fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: gqlVariables,
      }),
      method: "POST",
    });
    const result = await res.json();
    result.data.pokemons && setResult(result.data.pokemons);
  };

  useEffect(() => {
    Fetching();
    return () => {};
  }, []);

  useEffect(() => {
    result && console.log("57", result);
    return () => {};
  }, [result]);

  return <div className="container-pokemon-cards"></div>;
};

export default PokemonListPage;
