export const GCOLOR = {
  PRIMARY: "#333335",
  SECONDARY: "#dcdcdc",
  TERTIER: "#dcdcdc",
  GRAY: "#121212",
  BEIGE: "#D9CAB3",
};

/* Media Queries   0    1    2    3    4 */
export const GMQ = [320, 576, 768, 992, 1200];

export const GSPACER = {
  GUTTER: ["0.7rem", "0.95rem", "1rem", "1.2rem", "1.4rem"],
};

export const GFONT = {
  SIZE: [
    "calc(0.4em  + 0.50vw)",
    "calc(0.5em  + 0.60vw)",
    "calc(0.6em  + 0.70vw)",
    "calc(0.7em  + 0.80vw)",
    "calc(0.8em  + 0.90vw)",
    "calc(0.9em  + 1.00vw)",
    "calc(1.2em  + 1.20vw)",
    "calc(1.4em  + 1.40vw)",
  ],
};

export const mq = GMQ.map((mq) => `@media (min-width: ${mq}px)`);
