/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unused-vars */
import React, { useContext, useEffect, useState } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";

import styled from "@emotion/styled";
import { css } from "@emotion/css";
import { GCOLOR, GFONT, GSPACER, mq } from "../../styles/globalStyle";
import { gqlQuery } from "../Querying";

const PokemonListPage = () => {
  const [totalPokemons, setTotalPokemons] = useState(0);
  const [offset, setOffset] = useState(1);
  const [limit, setLimit] = useState(20);
  const [result, setResult] = useState();
  const { pokemons } = useContext(PokemonContext);

  const Fetching = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: {
          limit,
          offset,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setResult(res?.data?.pokemons));
  };

  useEffect(() => {
    Fetching();

    return () => {};
  }, [offset]);

  useEffect(() => {
    result && setTotalPokemons(result.count);
    return () => {};
  }, [result]);

  const Button = styled.button`
    background-color: ${GCOLOR.SECONDARY};
    border-radius: calc(0.25em + 0.15vw);
    border: 1px solid ${GCOLOR.TERTIER};
    color: ${GCOLOR.TERTIER};
    cursor: ${props.disabled ? "pointer" : "default"};
    margin-top: 0.4rem;
    padding-bottom: 0.6rem;
    padding-top: 0.6rem;

    ${mq[0]} {
      font-size: ${GFONT.SIZE0};
      padding-left: ${GSPACER.GUTTER[0]};
      padding-right: ${GSPACER.GUTTER[0]};
    }

    ${mq[3]} {
      font-size: ${GFONT.SIZE1};
      padding-left: ${GSPACER.GUTTER[3]};
      padding-right: ${GSPACER.GUTTER[3]};
    }

    padding: ${(props) => (props.gutter ? `0 10px` : "0")};
  `;

  const handleClickPrev = () => {
    setOffset((prev) => prev - 20);
  };

  const handleClickNext = () => {
    setOffset((prev) => prev + 20);
  };

  return (
    <div className={`container-cards ${css``}`}>
      <ul
        className={`list-cards dflex ${css`
          flex-wrap: wrap;
          justify-content: space-between;

          ${mq[0]} {
            padding: 1.2rem ${GSPACER.GUTTER[0]};
          }

          ${mq[3]} {
            padding: 3rem ${GSPACER.GUTTER[3]};
          }
        `}`}
      >
        {result &&
          result?.results?.map((item, key) => {
            return (
              <li
                key={key}
                className={`dflex flex-col pokemon-cards ${css`
                  align-items: center;
                  margin-top: 1rem;
                `}`}
              >
                <img src={item.image} alt="" />
                <p>{item.name}</p>
              </li>
            );
          })}
      </ul>

      <div
        className={`dflex flex-col wrapper-controllers ${css`
          align-items: flex-end;

          ${mq[0]} {
            padding: 1.2rem ${GSPACER.GUTTER[0]} 0;
          }

          ${mq[3]} {
            padding: 3rem ${GSPACER.GUTTER[3]} 0;
          }
        `}`}
      >
        {result && totalPokemons && <p>{totalPokemons} Pokemons</p>}

        <div className={`dflex wrapper-buttons`}>
          <Button
            className={`btn-prev ${css`
              cursor: ${offset <= 1 ? `default` : ``};
              opacity: ${offset <= 1 && 0.5};
            `}`}
            onClick={() => handleClickPrev()}
            disabled={offset <= 1}
          >
            Prev
          </Button>

          <button
            className={`btn btn-next ${css`
              cursor: ${offset >= totalPokemons - limit ? `default` : ``};
              margin-left: 1rem;
              opacity: ${offset >= totalPokemons - limit && 0.5};
            `}`}
            onClick={() => handleClickNext()}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default PokemonListPage;
