import styled from "@emotion/styled";
import { GFONT, GSPACER, mq } from "../../styles/globalStyle";

export const ContainerCards = styled.div`
  position: relative;
  width: 100%;

  ${mq[3]} {
    margin: 0 auto;
    max-width: var(--screen-max-width);
  }
`;

export const Img = styled.img`
  ${mq[0]} {
    transform: scale(0.6);
  }

  ${mq[3]} {
    transform: scale(0.75);
  }
`;

export const Li = styled.li`
  align-items: center;
  margin-top: 1rem;
  cursor: pointer;
`;

export const Ul = styled.ul`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  position: relative;

  ${mq[0]} {
    padding: 1.2rem ${GSPACER.GUTTER[2]};
  }

  ${mq[3]} {
    padding: 2.5rem ${GSPACER.GUTTER[3]};
  }
`;

export const P = styled.p`
  ${mq[0]} {
    font-size: ${GFONT.SIZE[1]};
  }

  ${mq[3]} {
    font-size: ${GFONT.SIZE[1]};
  }
`;

export const WrapperButton = styled.div`
  display: flex;
  justify-content: flex-end;
  position: relative;

  ${mq[0]} {
    padding: 1.2rem ${GSPACER.GUTTER[0]};
  }

  ${mq[3]} {
    margin: 0 auto;
    max-width: var(--screen-max-width);
    padding: 2.5rem ${GSPACER.GUTTER[3]};
  }
`;
