import React from "react";
import { css } from "@emotion/css";
import { ButtonClose, ContainerModal, Modal, Name, Ul } from "./style";

const PokemonModal = ({ data, img, modal }) => {
  return (
    <ContainerModal className="container-modal">
      <Modal className="modal">
        <ButtonClose
          className="btn-close"
          onClick={() => modal((prev) => !prev)}
        >
          âœ•
        </ButtonClose>

        <Name className="name">{data.name}</Name>

        <div
          className={`dflex content ${css`
            position: relative;
            flex-direction: column;
          `}`}
        >
          <div
            className={`wrapper-img ${css`
              position: relative;
              margin-left: auto;
              margin-right: auto;
            `}`}
          >
            <img className="pokemon-img" src={img} alt="pokemon-img" />
          </div>

          {data && (
            <div className="wrapper-information">
              <Ul className="information-abilities">
                Abilities:
                {data.abilities.map((ability, key) => (
                  <li key={key} className="ability">
                    {ability.ability.name}
                  </li>
                ))}
              </Ul>
            </div>
          )}
        </div>
      </Modal>
    </ContainerModal>
  );
};

export default PokemonModal;
