import styled from "@emotion/styled";
import { GCOLOR, GFONT, GSPACER, mq } from "../../styles/globalStyle";

export const ContainerCards = styled.div`
  position: relative;
  width: 100%;

  ${mq[3]} {
    margin: 0 auto;
    max-width: var(--screen-max-width);
  }
`;

export const Img = styled.img`
  ${mq[0]} {
    transform: scale(0.55);
  }

  ${mq[3]} {
    transform: scale(0.75);
  }
`;

export const Li = styled.li`
  align-items: center;
  cursor: pointer;
  display: flex;
  flex-direction: column;

  ${mq[3]} {
    margin-top: 0.75rem;
  }
`;

export const Ul = styled.ul`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  position: relative;

  ${mq[0]} {
    padding: 1.2rem ${GSPACER.GUTTER[0]};
  }

  ${mq[3]} {
    padding: 2.5rem ${GSPACER.GUTTER[3]};
  }
`;

export const P = styled.p`
  ${mq[0]} {
    font-size: ${GFONT.SIZE[2]};
  }

  ${mq[3]} {
    font-size: ${GFONT.SIZE[1]};
  }
`;

export const WrapperButton = styled.div`
  display: flex;
  justify-content: flex-end;
  position: relative;

  ${mq[0]} {
    padding: 1.2rem ${GSPACER.GUTTER[0]};
  }

  ${mq[3]} {
    margin: 0 auto;
    max-width: var(--screen-max-width);
    padding: 2.5rem ${GSPACER.GUTTER[3]};
  }
`;

export const WrapperLoadingScreen = styled.div`
  align-items: center;
  background: ${GCOLOR.BEIGE};
  bottom: 0;
  display: flex;
  justify-content: center;
  left: 0;
  min-height: 100vh;
  position: absolute;
  right: 0;
  top: 0;
  width: 100%;
`;

export const LoadingText = styled.p`
  color: ${GCOLOR.BLUEISH};
`;
