import { v4 as uuidv4 } from "uuid";

import { REDUCER } from "../constant/constantReducer";
const { ADD_POKEMON, RELEASE_POKEMON, POPULATE_POKEMON } = REDUCER;

const pokemonReducer = (state, action) => {
  switch (action.type) {
    case ADD_POKEMON: {
      return [
        ...state,
        {
          name: action.pokemon.name,
          id: uuidv4(),
        },
      ];
    }

    case RELEASE_POKEMON: {
      return state.filter((pokemon) => pokemon.id !== action.id);
    }

    case POPULATE_POKEMON: {
      return {
        img: action.pokemon.img,
        name: action.pokemon.name,
        data: action.pokemon.data,
      };
    }

    default:
      return state;
  }
};

export default pokemonReducer;
