/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unused-vars */
import React, { useContext, useEffect, useState } from "react";
import { PokemonContext } from "../../contexts/PokemonContext";

import { css } from "@emotion/css";
import { GFONT, GSPACER, mq } from "../../styles/globalStyle";
import { gqlQuery } from "../Querying";

const Button = (props) => {
  console.log("11", props);
  return (
    <button
      css={{
        color: "orange",
        fontSize: props.small ? 24 : 36,
        opacity: props.disabled ? 0.5 : 1,
      }}
      {...props}
    >
      Hello World!
    </button>
  );
};

const PokemonListPage = () => {
  const [offset, setOffset] = useState(1);
  const [limit, setLimit] = useState(20);
  const [result, setResult] = useState();
  const { pokemons } = useContext(PokemonContext);

  const Fetching = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: {
          limit,
          offset,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setResult(res?.data));
  };

  useEffect(() => {
    Fetching();
    console.log("35", offset);
    return () => {};
  }, [offset]);

  useEffect(() => {
    result && console.log(result?.pokemons);
    return () => {};
  }, [result]);

  const handleClickPrev = () => {
    setOffset((prev) => prev - 20);
  };

  const handleClickNext = () => {
    setOffset((prev) => prev + 20);
  };

  return (
    <div className={`container-cards ${css``}`}>
      <ul
        className={`list-cards dflex ${css`
          flex-wrap: wrap;
          justify-content: space-between;

          ${mq[0]} {
            padding: 1.2rem ${GSPACER.GUTTER[0]};
          }

          ${mq[3]} {
            padding: 3rem ${GSPACER.GUTTER[3]};
          }
        `}`}
      >
        {result &&
          result?.pokemons?.results?.map((item, key) => {
            return (
              <li
                key={key}
                className={`dflex flex-col pokemon-cards ${css`
                  align-items: center;

                  ${mq[0]} {
                    margin-top: 0.4rem;
                  }

                  ${mq[2]} {
                    margin-top: 0;
                  }
                `}`}
              >
                <img src={item.image} alt="" />
                <p>{item.name}</p>
              </li>
            );
          })}
      </ul>

      <div
        className={`dflex wrapper-buttons ${css`
          justify-content: flex-end;

          ${mq[0]} {
            padding: 1.2rem ${GSPACER.GUTTER[0]} 0;
          }

          ${mq[3]} {
            padding: 3rem ${GSPACER.GUTTER[3]} 0;
          }
        `}`}
      >
        <Button
          className={`btn-prev ${css``}`}
          onClick={() => handleClickPrev()}
          disabled={offset <= 1}
        >
          Prev
        </Button>

        <button
          className={`btn btn-next ${css`
            margin-left: 1rem;
          `}`}
          onClick={() => handleClickNext()}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default PokemonListPage;
