import { css } from "@emotion/css";
import React from "react";
import { ContainerModal } from "../../styles/container";
import { mq } from "../../styles/globalStyle";
import { P } from "../../styles/text";

const PokemonModal = ({ data, img, modal }) => {
  return (
    <ContainerModal className={`container-modal`}>
      <div
        className={`modal dflex flex-col ${css`
          background-color: #c6c6c6;
          border-radius: 2rem;
          height: 100%;
          position: relative;
          margin-left: auto;
          margin-right: auto;
          width: 25%;
        `}`}
      >
        <button
          className={`btn-close ${css`
            background: transparent;
            cursor: pointer;
            margin: 1.5rem 1.5rem 3rem auto;

            ${mq[3]} {
              margin-top: 1.5rem;
              margin-right: 1.5rem;
              margin-bottom: 5rem;
            }
          `}`}
          onClick={() => modal((prev) => !prev)}
        >
          âœ•
        </button>

        <div
          className={`dflex content ${css`
            position: relative;
          `}`}
        >
          <div
            className={`wrapper-img ${css`
              position: relative;
              flex-basis: 40%;
            `}`}
          >
            <img
              className={`pokemon-img ${css`
                transform: scale(1.1);
              `}`}
              src={img}
              alt="pokemon-img"
            />
          </div>

          {data && (
            <div
              className={`wrapper-information ${css`
                position: relative;
                flex-basis: auto;
              `}`}
            >
              <P className={`name`}>{data.name}</P>

              <div className="wrapper-abilities">
                {data.abilities.map((ability, key) => (
                  <P key={key} className={`ability`}>
                    {ability.ability.name}
                  </P>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </ContainerModal>
  );
};

export default PokemonModal;
