import React from "react";
import Navbar from "./components/Navbar";
import BookContextProvider from "./contexts/BookContext";
import BookList from "./components/BookList";
import NewBookForm from "./components/NewBookForm";

import { Global, CacheProvider, css } from "@emotion/react";

function App() {
  const GlobalStyles = css`
    body {
      background-color: red;
    }
  `;

  return (
    <div
      className={`App ${css`
        background: #4c2a4c;
        margin: 20px auto;
        width: 90%;
        max-width: 700px;
        color: #eee;
      `}`}
    >
      <Global
        styles={css`
          body {
            background-color: #fafafa;
          }
        `}
      />

      <Global
        styles={css`
          input[type="text"] {
            background: #3c1f3c;
            border: 0;
            box-sizing: border-box;
            color: #fff;
            margin: 6px 0;
            padding: 10px;
            width: 100%;
          }

          input[type="submit"] {
            background: #eee;
            border: 0;
            display: block;
            margin: 10px auto;
            padding: 6px 20px;
          }
        `}
      />
      <BookContextProvider>
        <Navbar />
        <BookList />
        <NewBookForm />
      </BookContextProvider>
    </div>
  );
}

export default App;
