import React from "react";
import Navbar from "./components/navbar/index";
import PokemonContextProvider from "./contexts/PokemonContext";
import PokemonForm from "./components/pokemonForm";
import PokemonListPage from "./components/list";

import { GLOBAL_COLOR } from "./styles/globalStyle";
import { Global, css } from "@emotion/react";

const GlobalStyles = css`
  h1,
  p {
    margin-block-start: 0;
    margin-block-end: 0;
  }

  input {
    border: 0;
    margin: 0;
    padding: 0;
  }

  button {
    border: 0;
    border-radius: 0;
    padding: 0;
  }

  input[type="text"] {
    background: ${GLOBAL_COLOR.SECONDARY};
    box-sizing: border-box;
    color: #fff;
    margin: 0.5rem 0;
    padding: 0.5rem 1rem;
    width: 100%;
  }
`;

function App() {
  return (
    <div className="App">
      <Global styles={GlobalStyles} />
      <PokemonContextProvider>
        <Navbar />
        <PokemonListPage />
        <PokemonForm />
      </PokemonContextProvider>
      {/* <BookContextProvider>
        <Navbar />
        <BookList />
        <NewBookForm />
      </BookContextProvider> */}
    </div>
  );
}

export default App;
