/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from "react";
import { gqlQuery, gqlQueryAbilites } from "../Querying";

import { css } from "@emotion/css";
import { GSPACER, mq } from "../../styles/globalStyle";
import { Button } from "../../styles/button";
import { Container } from "../../styles/container";
import { P } from "../../styles/text";

const PokemonListPage = () => {
  const [offset, setOffset] = useState(1);
  const [limit, setLimit] = useState(20);
  const [result, setResult] = useState();

  const [pokemonName, setPokemonName] = useState("ditto");
  const [pokemonDetails, setPokemonDetails] = useState();
  const [openModal, setOpenModal] = useState(false);

  const Fetching = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: {
          limit,
          offset,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setResult(res?.data?.pokemons));
  };

  const FetchingPokemonDetails = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQueryAbilites,
        variables: {
          pokemonName,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setPokemonDetails(res?.data?.pokemon));
  };

  useEffect(() => {
    Fetching();

    return () => {};
  }, [offset]);

  useEffect(() => {
    FetchingPokemonDetails();

    return () => {};
  }, [pokemonName]);

  useEffect(() => {
    pokemonDetails && console.log(pokemonDetails);
    return () => {};
  }, [pokemonDetails]);

  // setter getter pokemonName when user clicks
  const handleClickPokemon = (name) => {
    setPokemonName(name);
    setOpenModal((prev) => !prev);
  };

  const handleClickPrev = () => {
    setOffset((prev) => prev - 20);
  };

  const handleClickNext = () => {
    setOffset((prev) => prev + 20);
  };

  return (
    <Container
      className={`container-cards ${css`
        width: 100%;
      `}`}
    >
      <ul
        className={`list-cards dflex ${css`
          flex-wrap: wrap;
          justify-content: space-between;

          ${mq[0]} {
            padding-top: 1.2rem;
            padding-bottom: 1.2rem;
          }

          ${mq[3]} {
            padding-top: 2.5rem;
            padding-bottom: 2.5rem;
          }
        `}`}
      >
        {result &&
          result?.results?.map((item, key) => {
            return (
              <li
                // get pokemon name
                onClick={() => handleClickPokemon(item.name)}
                key={key}
                className={`dflex flex-col pokemon-cards ${css`
                  align-items: center;
                  margin-top: 1rem;
                  cursor: pointer;
                `}`}
              >
                <img src={item.image} alt="" />
                <p>{item.name}</p>
              </li>
            );
          })}
      </ul>

      <div
        className={`dflex wrapper-buttons ${css`
          justify-content: flex-end;

          ${mq[0]} {
            padding-top: 1.2rem;
            padding-bottom: 1.2rem;
          }

          ${mq[3]} {
            padding-top: 2.5rem;
            padding-bottom: 2.5rem;
          }
        `}`}
      >
        <Button
          className={`btn-prev`}
          onClick={() => handleClickPrev()}
          disabled={offset <= 1}
        >
          Prev
        </Button>

        <Button
          className={`btn-next ${css`
            margin-left: 1rem;
          `}`}
          onClick={() => handleClickNext()}
        >
          Next
        </Button>
      </div>

      {openModal && (
        <div
          className={`container-modal ${css`
            left: 0;
            position: absolute;
            right: 0;
            top: 0;

            z-index: 1;
            padding-left: ${GSPACER.GUTTER[3]};
            padding-right: ${GSPACER.GUTTER[3]};
          `}`}
        >
          <div
            className={`modal-content ${css`
              height: 100%;
              background-color: #c6c6c6;
              width: 100%;
            `}`}
          >
            <span
              className={`dflex ${css`
                cursor: pointer;
                justify-content: flex-end;
                margin-right: 1rem;
                margin-top: 1rem;
                position: relative;
              `}`}
              onClick={() => setOpenModal((prev) => !prev)}
            >
              ✕
            </span>
            {pokemonDetails && (
              <>
                <P className={`name`}>{pokemonDetails.name}</P>

                <div className="wrapper-abilities">
                  {pokemonDetails.abilities.map((ability, key) => (
                    <P key={key} className={`ability`}>
                      {ability.ability.name}
                    </P>
                  ))}
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </Container>
  );
};

export default PokemonListPage;
