import React, { useState } from "react";
import { css } from "@emotion/css";
import {
  ButtonBack,
  ContainerModal,
  Li,
  Modal,
  Name,
  SmallNote,
  Ul,
  WrapperInformation,
} from "./style";
import { IconBack } from "../../assets/IconPack";
import { GCOLOR } from "../../styles/globalStyle";

const PokemonDetailsPage = ({ data, img, modal }) => {
  const [limitMoves] = useState(15);

  return (
    <ContainerModal className="container-modal">
      <Modal className="modal">
        <ButtonBack className="btn-back" onClick={() => modal((prev) => !prev)}>
          <IconBack />
        </ButtonBack>

        <Name className="name">{data.name}</Name>

        <div
          className={`dflex content ${css`
            position: relative;
            flex-direction: column;
          `}`}
        >
          <div
            className={`wrapper-img ${css`
              position: relative;
              margin-left: auto;
              margin-right: auto;
            `}`}
          >
            <img className="pokemon-img" src={img} alt="pokemon-img" />
          </div>

          {data && (
            <WrapperInformation className="wrapper-information">
              <div className="information-abilities">
                Abilities:
                <Ul>
                  {data.abilities.map((ability, key) => (
                    <Li key={key} className="list-ability">
                      {ability.ability.name}
                    </Li>
                  ))}
                </Ul>
              </div>
              <div className="information-moves">
                Moves:
                <Ul>
                  {data.moves.map(
                    (move, key) =>
                      key < limitMoves && (
                        <Li key={key} className="list-move">
                          {move.move.name}
                        </Li>
                      )
                  )}
                </Ul>
                <SmallNote>
                  only shows {limitMoves} from {data.moves.length} moves
                </SmallNote>
              </div>
              <div className="information-types">
                Types:
                <Ul>
                  {data.types.map((type, index) => (
                    <Li key={index} className="list-type">
                      {type.type.name}
                    </Li>
                  ))}
                </Ul>
              </div>
            </WrapperInformation>
          )}
        </div>
      </Modal>
    </ContainerModal>
  );
};

export default PokemonDetailsPage;
