import React from "react";
import Navbar from "./components/title/index";
import PokemonContextProvider from "./contexts/PokemonContext";
import PokemonForm from "./components/pokemonForm";
import PokemonListPage from "./components/list";

import { GCOLOR } from "./styles/globalStyle";
import { Global, css } from "@emotion/react";

const GlobalStyles = css`
  * {
    box-sizing: border-box;

    &:focus {
      outline: none;
    }
  }

  h1,
  h2,
  p {
    color: ${GCOLOR.TERTIER};
    margin-block-start: 0;
    margin-block-end: 0;
  }

  ul {
    margin: 0;
    padding-inline-start: 0;
  }

  input,
  button {
    border: 0;
    border-radius: 0;
    padding: 0;
  }

  ul {
    list-style-type: none;
  }

  .btn {
    background-color: ${GCOLOR.SECONDARY};
    border-radius: calc(0.25em + 0.15vw);
    border: 1px solid ${GCOLOR.TERTIER};
    color: ${GCOLOR.TERTIER};
    margin-top: 0.4rem;
    padding-bottom: 0.6rem;
    padding-top: 0.6rem;
  }

  .dflex {
    display: flex;
    position: relative;
  }

  .flex-col {
    flex-direction: column;
  }

  .flex-row {
    flex-direction: row;
  }
`;

function App() {
  return (
    <div className="App">
      <Global styles={GlobalStyles} />
      <PokemonContextProvider>
        <Navbar />
        <PokemonListPage />
        <PokemonForm />
      </PokemonContextProvider>
      {/* <BookContextProvider>
        <Navbar />
        <BookList />
        <NewBookForm />
      </BookContextProvider> */}
    </div>
  );
}

export default App;
