import styled from "@emotion/styled";
import { GCOLOR, GFONT, GSPACER, mq } from "../../styles/globalStyle";

export const DetailPage = styled.div`
  bottom: 0;
  left: 0;
  min-height: 100vh;
  padding-left: ${GSPACER.GUTTER[0]};
  padding-right: ${GSPACER.GUTTER[0]};
  position: absolute;
  right: 0;
  top: 0;
  width: 100%;
  z-index: 1;

  ${mq[3]} {
    padding-left: ${GSPACER.GUTTER[3]};
    padding-right: ${GSPACER.GUTTER[3]};
  }
`;

export const ButtonBack = styled.button`
  align-items center;
  background: transparent;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  height: 60px;
  justify-content: center;
  position: relative;
  width: 60px;
  margin-bottom: 0.5rem;

  ${mq[3]} {
    height: 85px;
    width: 85px;
    margin-bottom: 1rem;
  }
`;

export const Name = styled.p`
  color: ${GCOLOR.TERTIER};
  font-size: ${GFONT.SIZE[3]};
  font-weight: 600;
  letter-spacing: 0.5px;
  text-transform: uppercase;

  ${mq[3]} {
    font-size: ${GFONT.SIZE[6]};
    letter-spacing: 0.8px;
  }
`;

export const Container = styled.div`
  display: grid;
  grid-template-areas:
    "one two ."
    "one two ."
    "three four ."
    "three four .";
  position: relative;
  width: 100%;
`;

export const Img = styled.img`
  padding-top: 0.5rem;
  max-width: 100%;
  grid-area: one;

  ${mq[3]} {
    transform: scale(1.1);
  }
`;

export const ULAbilities = styled.ul`
  grid-area: two;
`;

export const ULMoves = styled.ul`
  grid-area: three;
`;

export const ULTypes = styled.ul`
  grid-area: four;
`;

export const Li = styled.li`
  color: ${GCOLOR.TERTIER};
  padding-left: 1rem;
  min-width: 50%;

  &:before {
    position: absolute;
    left: 0;
  }
`;

export const Title = styled.p`
  color: ${GCOLOR.TERTIER};
  padding-bottom: 0.4rem;
`;
