import React, { useReducer } from "react";

const initialState = { offset: 1 };

const pokemonsListReducer = (state, action) => {
  switch (action.type) {
    case "LOWER_OFFSET": {
      return { offset: state.offset - 20 };
    }

    case "RAISE_OFFSET": {
      return { offset: state.offset + 20 };
    }

    default:
      return state;
  }
};

export default pokemonsListReducer;
