import styled from "@emotion/styled";
import { GCOLOR, GFONT, GSPACER, mq } from "../../styles/globalStyle";

export const Container = styled.div`
  position: relative;
`;

export const H1 = styled.h1`
  position: relative;
  margin-left: auto;
  margin-right: auto;
  text-align: center;

  ${mq[0]} {
    padding-right: ${GSPACER.GUTTER[0]};
  }

  ${mq[3]} {
    padding-right: ${GSPACER.GUTTER[3]};
  }
`;

export const Content = styled.div`
  display: flex;
  justify-content: space-between;
`;

export const WrapperAbilities = styled.div``;
export const WrapperMoves = styled.div``;
export const WrapperTypes = styled.div``;
