import { css } from "@emotion/css";
import React from "react";
import { GSPACER } from "../../styles/globalStyle";
import { P } from "../../styles/text";

const PokemonModal = ({ data, img, modal }) => {
  return (
    <div
      className={`container-modal ${css`
        bottom: 0;
        left: 0;
        padding-left: ${GSPACER.GUTTER[3]};
        padding-right: ${GSPACER.GUTTER[3]};
        position: absolute;
        right: 0;
        top: 0;
        z-index: 1;
      `}`}
    >
      <div
        className={`modal-content ${css`
          background-color: #c6c6c6;
          height: 100%;
          width: 100%;
          position: relative;
        `}`}
      >
        <span
          className={`dflex ${css`
            cursor: pointer;
            position: absolute;x
            top: 5%;
            right: 3%;
          `}`}
          onClick={() => modal((prev) => !prev)}
        >
          âœ•
        </span>

        <img src={img} alt="pokemon-img" />

        {data && (
          <>
            <P className={`name`}>{data.name}</P>

            <div className="wrapper-abilities">
              {data.abilities.map((ability, key) => (
                <P key={key} className={`ability`}>
                  {ability.ability.name}
                </P>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default PokemonModal;
