/* eslint-disable react-hooks/exhaustive-deps */
import React, { useContext, useEffect, useState } from "react";
import { gqlQuery, gqlQueryAbilites } from "../PokemonQuerying";

import PokemonModal from "../pokemon-modal";

import { Button } from "../../styles/button";
import { ContainerCards, Img, Li, P, Ul, WrapperButton } from "./style";
import { PokemonContext } from "../../contexts/PokemonContext";

const PokemonListPage = () => {
  const { dispatch } = useContext(PokemonContext);

  const [offset, setOffset] = useState(1);
  const [limit] = useState(20);
  const [result, setResult] = useState();

  const [pokemonName, setPokemonName] = useState("ditto");
  const [pokemonImg, setPokemonImg] = useState();
  const [pokemonDetails, setPokemonDetails] = useState();
  const [openModal, setOpenModal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const Fetching = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQuery,
        variables: {
          limit,
          offset,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setResult(res?.data?.pokemons));
  };

  const FetchingPokemonDetails = () => {
    fetch("https://graphql-pokeapi.graphcdn.app/", {
      credentials: "omit",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        query: gqlQueryAbilites,
        variables: {
          pokemonName,
        },
      }),
      method: "POST",
    })
      .then((res) => res.json())
      .then((res) => setPokemonDetails(res?.data?.pokemon));
  };

  useEffect(() => {
    Fetching();
    return () => {};
  }, [Fetching, offset]);

  useEffect(() => {
    FetchingPokemonDetails();
    return () => {};
  }, [pokemonName]);

  useEffect(() => {
    pokemonDetails && console.log("68", pokemonDetails);
    return () => {};
  }, [pokemonDetails]);

  const handleClickPokemon = (item) => {
    // extract pokemon name, and image
    setPokemonImg(item.image);
    setPokemonName(item.name);

    if (pokemonDetails) {
      setIsLoading(true);
      setTimeout(() => {
        dispatch({
          type: "POPULATE POKEMON",
          pokemons: {
            data: pokemonDetails,
            image: item.image,
            name: item.name,
          },
        });

        setOpenModal((prev) => !prev);
      }, 6000);
    }
  };

  const handleClickPrev = () => {
    setOffset((prev) => prev - 20);
  };

  const handleClickNext = () => {
    setOffset((prev) => prev + 20);
  };

  return (
    <ContainerCards className="container-cards">
      {!openModal && (
        <>
          <Ul className="list-cards">
            {result &&
              result?.results?.map((item, key) => {
                return (
                  <Li
                    // get pokemon information more details, show modal
                    onClick={() => handleClickPokemon(item)}
                    key={key}
                    className={`pokemon-cards`}
                  >
                    <Img className="" src={item.image} alt="img" />
                    <P>{item.name}</P>
                  </Li>
                );
              })}
          </Ul>

          <WrapperButton className="wrapper-buttons">
            <Button
              className="btn-prev"
              onClick={() => handleClickPrev()}
              disabled={offset <= 1}
            >
              Prev
            </Button>

            <Button className="btn-next" onClick={() => handleClickNext()}>
              Next
            </Button>
          </WrapperButton>
        </>
      )}

      {/* {openModal && (
        <PokemonModal
          data={pokemonDetails}
          img={pokemonImg}
          modal={setOpenModal}
        />
      )} */}
    </ContainerCards>
  );
};

export default PokemonListPage;
